$(document).on({
    mouseenter: function(){
        function resolverDepoisDe1Segundos(x) {
            return new Promise(resolve => {
                setTimeout(() => {
                    $('.box-whatsapp').css("width","160px");
                }, 500);
            });
        }

        function resolverDepoisDe2Segundos(x) {
            return new Promise(resolve => {
                setTimeout(() => {
                    $('.box-whatsapp').find('.aviso').css("display","inline-block");
                }, 700);
            });
        }

        async function adicionar1(x) {
            resolverDepoisDe1Segundos(20);
            resolverDepoisDe2Segundos(30);
        }
        adicionar1(10)					
    },
    mouseleave: function(){
        function resolverDepoisDe1Segundos(x) {
            return new Promise(resolve => {
                setTimeout(() => {
                    $('.box-whatsapp').find('.aviso').css("display","none");
                }, 500);
            });
        }

        function resolverDepoisDe2Segundos(x) {
            return new Promise(resolve => {
                setTimeout(() => {
                    $('.box-whatsapp').css("width","60px");
                }, 700);
            });
        }

        async function adicionar1(x) {
            resolverDepoisDe1Segundos(20);
            resolverDepoisDe2Segundos(30);
        }
        adicionar1(10)
        
    }
}, '.box-whatsapp');